const ransomwareDB = $('Ransomware Database').all().map(n => n.json);
const telemetry = $('Query Endpoint Telemetry').all().map(n => n.json);
const detections = [];

// Detection patterns for ransomware activity
const detectionPatterns = [
  {
    ransomware: 'Akira',
    patterns: [
      { indicator: /vssadmin.*delete.*shadow/i, weight: 0.5, description: 'Shadow copy deletion attempt' },
      { indicator: /cipher.*\/w:/i, weight: 0.4, description: 'Data wiping attempt' },
      { indicator: /bcdedit.*recoveryenabled.*no/i, weight: 0.4, description: 'Recovery disabled' },
      { indicator: /\.akira$/i, weight: 0.6, description: 'Akira encrypted file' },
      { indicator: /readme.*\.txt$/i, weight: 0.3, description: 'Potential ransom note' },
      { indicator: /stop.*vmware.*esxi/i, weight: 0.3, description: 'ESXi process termination' }
    ]
  },
  {
    ransomware: 'Qilin',
    patterns: [
      { indicator: /\.qilin$/i, weight: 0.6, description: 'Qilin encrypted file' },
      { indicator: /readme_qilin/i, weight: 0.5, description: 'Qilin ransom note' },
      { indicator: /powershell.*-enc.*base64/i, weight: 0.3, description: 'Encoded PowerShell payload' }
    ]
  },
  {
    ransomware: 'General Ransomware',
    patterns: [
      { indicator: /taskkill.*\/f.*\/im.*avp/i, weight: 0.4, description: 'Killing AV processes' },
      { indicator: /net.*stop.*WindowsDefender/i, weight: 0.3, description: 'Disabling Defender' },
      { indicator: /sc.*stop.*MsMpEngine/i, weight: 0.3, description: 'Disabling AV engine' },
      { indicator: /icacls.*\/grant.*f/i, weight: 0.3, description: 'Taking ownership' },
      { indicator: /takeown.*\/f.*\/r/i, weight: 0.3, description: 'Ownership acquisition' },
      { indicator: /\\$rand.*\.txt$/i, weight: 0.4, description: 'Suspicious text file' },
      { indicator: /how.to.restore.*your.files/i, weight: 0.5, description: 'Ransom note text' }
    ]
  },
  {
    ransomware: 'Clop',
    patterns: [
      { indicator: /clop.*readme/i, weight: 0.5, description: 'Clop ransom note' },
      { indicator: /\.clop$/i, weight: 0.6, description: 'Clop encrypted file' },
      { indicator: /mfa.*fatigue/i, weight: 0.3, description: 'MFA fatigue attack pattern' }
    ]
  }
];

// Scan telemetry for ransomware indicators
for (const event of telemetry) {
  const eventString = JSON.stringify(event).toLowerCase();

  for (const rule of detectionPatterns) {
    let totalWeight = 0;
    let matchedPatterns = [];

    for (const pattern of rule.patterns) {
      if (pattern.indicator.test(eventString)) {
        totalWeight += pattern.weight;
        matchedPatterns.push(pattern.description);
      }
    }

    if (totalWeight >= 0.5) {
      const ransomwareInfo = ransomwareDB.find(r => r.name === rule.ransomware);
      const isEarlyStage = totalWeight < 0.7;

      detections.push({
        detectionId: `RANSOM-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
        timestamp: new Date().toISOString(),
        ransomwareType: rule.ransomware,
        ransomwareGroup: ransomwareInfo?.name || 'Unknown',
        confidence: Math.min(totalWeight, 1.0),
        attackPhase: isEarlyStage ? 'EARLY' : 'ACTIVE_ENCRYPTION',
        matchedPatterns: matchedPatterns,
        originalEvent: event,
        severity: ransomwareInfo?.severity || 'HIGH',
        falsePositiveAnalysis: {
          riskLevel: 'LOW',
          potentialFalsePositives: [
            'Legitimate backup operations using vssadmin',
            'Scheduled disk optimization using cipher',
            'Normal system administration scripts'
          ],
          validationChecks: [
            'Verify against scheduled backup jobs',
            'Check for legitimate disk cleanup operations',
            'Cross-reference with change management system'
          ]
        },
        businessContext: {
          affectedSystems: determineAffectedSystems(event),
          estimatedImpact: determineBusinessImpact(ransomwareInfo, isEarlyStage),
          recoveryTime: ransomwareInfo?.doubleExtortion ? 'Weeks' : 'Days',
          dataTheftRisk: ransomwareInfo?.doubleExtortion ? 'HIGH' : 'MEDIUM'
        },
        recommendedActions: generateRansomwareResponse(ransomwareInfo, isEarlyStage),
        enrichmentData: {
          knownIoCs: ransomwareInfo?.iocs || {},
          attackVectors: ransomwareInfo?.attackVector || [],
          targetIndustries: ransomwareInfo?.targetIndustries || [],
          doubleExtortion: ransomwareInfo?.doubleExtortion || false
        }
      });
    }
  }
}

return detections.map(d => ({ json: d }));

function determineAffectedSystems(event) {
  const systems = [];
  if (event.hostname?.includes('ESXi') || event.process_name?.includes('vmware')) {
    systems.push('VMware ESXi');
  }
  if (event.os_platform === 'windows') {
    systems.push('Windows Endpoints');
  }
  if (event.os_platform === 'linux') {
    systems.push('Linux Servers');
  }
  if (event.department === 'file_server') {
    systems.push('File Servers');
  }
  return systems;
}

function determineBusinessImpact(ransomwareInfo, isEarlyStage) {
  if (!ransomwareInfo) return 'Unknown - requires investigation';

  const baseImpact = ransomwareInfo.severity === 'CRITICAL' ? 'CRITICAL' : 'HIGH';

  if (isEarlyStage) {
    return `${baseImpact} - Early detection allows containment`;
  }

  if (ransomwareInfo.doubleExtortion) {
    return `${baseImpact} - Data theft AND encryption confirmed`;
  }

  return `${baseImpact} - Active encryption in progress`;
}

function generateRansomwareResponse(ransomwareInfo, isEarlyStage) {
  const actions = [];

  if (isEarlyStage) {
    actions.push({
      priority: 'IMMEDIATE',
      action: 'Isolate affected systems from network',
      rationale: 'Prevent lateral movement and further encryption'
    });
    actions.push({
      priority: 'IMMEDIATE', 
      action: 'Block outbound traffic to Tor/exfiltration IPs',
      rationale: 'Prevent data exfiltration'
    });
  } else {
    actions.push({
      priority: 'CRITICAL',
      action: 'DECIDE: Pay ransom OR Restore from backups',
      rationale: 'Active encryption detected - immediate decision required'
    });
    actions.push({
      priority: 'CRITICAL',
      action: 'Engage incident response team immediately',
      rationale: 'Full ransomware attack in progress'
    });
  }

  actions.push({
    priority: 'HIGH',
    action: 'Preserve forensic evidence',
    rationale: 'Memory dumps, disk images, network logs needed'
  });

  actions.push({
    priority: 'HIGH',
    action: 'Check backup integrity',
    rationale: 'Determine recovery options and backup status'
  });

  if (ransomwareInfo?.doubleExtortion) {
    actions.push({
      priority: 'HIGH',
      action: 'Monitor for data leak site posts',
      rationale: 'Double extortion - data may be published'
    });
  }

  return actions;
}

