parameters.functionCode:
const items = $input.all();
const summary = {
  reportDate: new Date().toISOString(),
  scanPeriod: 'Last 24 hours',
  ransomwareGroupsMonitored: 5,
  detectionSummary: {
    totalAlerts: items.length,
    byGroup: {},
    byPhase: { EARLY: 0, ACTIVE_ENCRYPTION: 0 },
    bySeverity: { CRITICAL: 0, HIGH: 0, MEDIUM: 0, LOW: 0 }
  },
  falsePositiveMetrics: {
    falsePositivesIdentified: 0,
    validationChecksRun: 0
  },
  businessImpactSummary: {
    systemsAtRisk: 0,
    doubleExtortionCases: 0,
    earlyStageDetections: 0
  },
  recommendations: []
};

for (const item of items) {
  const alert = item.json;

  // Group by ransomware
  if (!summary.detectionSummary.byGroup[alert.ransomwareGroup]) {
    summary.detectionSummary.byGroup[alert.ransomwareGroup] = 0;
  }
  summary.detectionSummary.byGroup[alert.ransomwareGroup]++;

  // By phase
  summary.detectionSummary.byPhase[alert.attackPhase]++;

  // By severity
  summary.detectionSummary.bySeverity[alert.severity]++;

  // Business impact
  if (alert.attackPhase === 'ACTIVE_ENCRYPTION') {
    summary.businessImpactSummary.doubleExtortionCases += 
      alert.enrichmentData?.doubleExtortion ? 1 : 0;
  }
  if (alert.attackPhase === 'EARLY') {
    summary.businessImpactSummary.earlyStageDetections++;
  }
  summary.businessImpactSummary.systemsAtRisk += 
    alert.businessContext?.affectedSystems?.length || 0;

  // False positive tracking
  if (alert.falsePositiveAnalysis?.potentialFalsePositives) {
    summary.falsePositiveMetrics.falsePositivesIdentified += 
      alert.falsePositiveAnalysis.potentialFalsePositives.length;
    summary.falsePositiveMetrics.validationChecksRun += 
      alert.falsePositiveAnalysis.validationChecks?.length || 0;
  }
}

// Generate recommendations
if (summary.detectionSummary.byPhase.ACTIVE_ENCRYPTION > 0) {
  summary.recommendations.push({
    priority: 'CRITICAL',
    recommendation: 'Activate full incident response plan',
    rationale: `${summary.detectionSummary.byPhase.ACTIVE_ENCRYPTION} active encryption events detected`
  });
}

if (summary.businessImpactSummary.earlyStageDetections > 0) {
  summary.recommendations.push({
    priority: 'HIGH',
    recommendation: 'Investigate early-stage detections',
    rationale: `${summary.businessImpactSummary.earlyStageDetections} early-stage detections allowed proactive response`
  });
}

if (summary.detectionSummary.byGroup['Akira'] > 0) {
  summary.recommendations.push({
    priority: 'HIGH',
    recommendation: 'Enhanced monitoring for Akira indicators',
    rationale: 'Akira is the most prevalent ransomware variant (34% of attacks)'
  });
}

summary.recommendations.push({
  priority: 'MEDIUM',
  recommendation: 'Verify backup integrity across all systems',
  rationale: 'Ensure recovery capability in case of successful attack'
});

return [{ json: summary }];
