const threats = items[0].json;

const analyzedThreats = [];

// Analyze React2Shell vulnerability
if (threats.vulnerabilities && threats.vulnerabilities.includes('CVE-2025-55182')) {
  analyzedThreats.push({
    threatId: 'THREAT-001',
    name: 'React2Shell',
    type: 'VULNERABILITY',
    severity: 'CRITICAL',
    cvss: 10.0,
    affectedSystems: ['React 19.x', 'Next.js 15.x/16.x'],
    requiresImmediateAction: true,
    recommendedResponse: ['patch', 'waf_rule', 'monitoring']
  });
}

// Analyze Gogs Zero-Day
if (threats.vulnerabilities && threats.vulnerabilities.includes('CVE-2025-8110')) {
  analyzedThreats.push({
    threatId: 'THREAT-002',
    name: 'Gogs Zero-Day RCE',
    type: 'VULNERABILITY',
    severity: 'CRITICAL',
    cvss: 9.8,
    affectedSystems: ['Gogs self-hosted Git'],
    requiresImmediateAction: true,
    recommendedResponse: ['network_isolation', 'access_control']
  });
}

// Analyze Ransomware threats
const ransomwareGroups = ['Gentlemen', 'Qilin', 'Rhysida', 'Akira', 'INC Ransom'];
ransomwareGroups.forEach(group => {
  if (threats.ransomware && threats.ransomware.includes(group)) {
    analyzedThreats.push({
      threatId: `RANSOMWARE-${group}`,
      name: `${group} Ransomware`,
      type: 'RANSOMWARE',
      severity: 'HIGH',
      affectedSystems: ['Windows', 'Linux', 'Network Shares'],
      requiresImmediateAction: true,
      recommendedResponse: ['backup_check', 'network_segmentation', 'alert']
    });
  }
});

// Analyze APT threats
const aptGroups = ['Infy', 'Mustang Panda', 'MuddyWater', 'BlindEagle'];
aptGroups.forEach(group => {
  if (threats.apt && threats.apt.includes(group)) {
    analyzedThreats.push({
      threatId: `APT-${group}`,
      name: `${group} APT`,
      type: 'APT',
      severity: 'CRITICAL',
      affectedSystems: ['Enterprise Network', 'Endpoints', 'Servers'],
      requiresImmediateAction: true,
      recommendedResponse: ['ioc_hunting', 'network_blocking', 'forensics']
    });
  }
});

return analyzedThreats.map(threat => ({ json: threat }));

const threats = items.map(item => item.json);

const assessedThreats = threats.map(threat => {
  let severity = threat.severity;
  let businessImpact = 'MEDIUM';
  let falsePositiveLikelihood = 'LOW';

  // Assess based on CVSS score
  if (threat.cvss >= 9.0) {
    severity = 'CRITICAL';
    businessImpact = 'CRITICAL';
  } else if (threat.cvss >= 7.0) {
    severity = 'HIGH';
    businessImpact = 'HIGH';
  } else if (threat.cvss >= 4.0) {
    severity = 'MEDIUM';
    businessImpact = 'MEDIUM';
  }

  // Adjust for threat type
  if (threat.type === 'APT' || threat.type === 'RANSOMWARE') {
    businessImpact = 'CRITICAL';
    falsePositiveLikelihood = 'MEDIUM';
  }

  // Adjust for exploitation status
  if (threat.requiresImmediateAction) {
    businessImpact = 'CRITICAL';
  }

  return {
    ...threat,
    assessedSeverity: severity,
    businessImpact,
    falsePositiveLikelihood,
    assessmentTimestamp: new Date().toISOString(),
    confidenceScore: 1 - (falsePositiveLikelihood === 'HIGH' ? 0.3 : falsePositiveLikelihood === 'MEDIUM' ? 0.15 : 0.05)
  };
});

return assessedThreats.map(threat => ({ json: threat }));

const threat = items[0].json;
const responses = [];

// Generate automated response actions based on threat type
switch (threat.type) {
  case 'VULNERABILITY':
    // Patch deployment
    responses.push({
      action: 'PATCH_DEPLOYMENT',
      priority: 'P0',
      target: threat.affectedSystems,
      automation: 'automatic',
      rollbackPlan: 'available',
      estimatedDowntime: '15-30 minutes'
    });

    // WAF rule deployment
    responses.push({
      action: 'WAF_RULE_DEPLOYMENT',
      priority: 'P0',
      target: 'Edge Security',
      automation: 'automatic',
      rules: ['block_rce_patterns', 'sanitize_inputs']
    });

    // Network isolation if critical
    if (threat.cvss >= 9.0) {
      responses.push({
        action: 'NETWORK_ISOLATION',
        priority: 'P0',
        target: 'Affected Systems',
        automation: 'semi-automatic',
        requiresApproval: 'Security Lead'
      });
    }
    break;

  case 'RANSOMWARE':
    // Backup verification
    responses.push({
      action: 'BACKUP_VERIFICATION',
      priority: 'P0',
      target: 'All Critical Systems',
      automation: 'automatic',
      checks: ['offline_backups', 'backup_integrity', 'restore_test']
    });

    // Network segmentation
    responses.push({
      action: 'NETWORK_SEGMENTATION',
      priority: 'P0',
      target: 'Affected Segments',
      automation: 'semi-automatic',
      requiresApproval: 'Network Team'
    });

    // Threat hunting
    responses.push({
      action: 'THREAT_HUNTING',
      priority: 'P1',
      target: 'Enterprise Network',
      automation: 'automatic',
      indicators: threat.recommendedResponse
    });

    // Alert escalation
    responses.push({
      action: 'ALERT_ESCALATION',
      priority: 'P0',
      target: 'SOC, IR Team, Leadership',
      automation: 'automatic',
      communication: ['email', 'slack', 'phone']
    });
    break;

  case 'APT':
    // IOC blocking
    responses.push({
      action: 'IOC_BLOCKING',
      priority: 'P0',
      target: ['Firewall', 'EDR', 'SIEM'],
      automation: 'automatic',
      scope: 'Enterprise Wide'
    });

    // Forensic collection
    responses.push({
      action: 'FORENSIC_COLLECTION',
      priority: 'P1',
      target: 'Affected Systems',
      automation: 'semi-automatic',
      artifacts: ['memory_dump', 'disk_image', 'network_logs']
    });

    // Account isolation
    responses.push({
      action: 'ACCOUNT_ISOLATION',
      priority: 'P0',
      target: 'Compromised Accounts',
      automation: 'semi-automatic',
      requiresApproval: 'Identity Team'
    });

    // Intelligence sharing
    responses.push({
      action: 'INTELLIGENCE_SHARING',
      priority: 'P2',
      target: ['ISACs', 'Government Agencies'],
      automation: 'manual',
      format: 'STIX/TAXII'
    });
    break;

  case 'PHISHING':
    // Email filtering
    responses.push({
      action: 'EMAIL_FILTERING',
      priority: 'P0',
      target: 'Email Gateway',
      automation: 'automatic',
      rules: ['block_sender', 'quarantine_attachments', 'url_sandboxing']
    });

    // User notification
    responses.push({
      action: 'USER_NOTIFICATION',
      priority: 'P1',
      target: 'Affected Users',
      automation: 'automatic',
      channel: ['email', 'sms', 'portal']
    });

    // Credential reset
    responses.push({
      action: 'CREDENTIAL_RESET',
      priority: 'P1',
      target: 'Potentially Compromised Accounts',
      automation: 'semi-automatic',
      requiresVerification: true
    });
    break;
}

// Generate incident report
responses.push({
  action: 'INCIDENT_REPORT_GENERATION',
  priority: 'P1',
  target: 'Documentation',
  automation: 'automatic',
  sections: ['executive_summary', 'technical_details', 'impact_assessment', 'response_actions', 'recommendations']
});

return responses.map(response => ({
  json: {
    threatId: threat.threatId,
    threatName: threat.name,
    severity: threat.assessedSeverity,
    ...response,
    generatedAt: new Date().toISOString(),
    workflowId: 'cybersentinel-automated-response-v1'
  }
}));

const threat = items[0].json;
const responses = [];

// Generate response actions for HIGH severity threats
responses.push({
  action: 'ALERT_GENERATION',
  priority: 'P1',
  target: 'Security Operations',
  automation: 'automatic',
  channels: ['email', 'slack']
});

responses.push({
  action: 'LOG_ENRICHMENT',
  priority: 'P2',
  target: 'SIEM',
  automation: 'automatic',
  enrichmentFields: ['threat_context', 'ioc_list', 'recommended_actions']
});

responses.push({
  action: 'MONITORING_ENHANCEMENT',
  priority: 'P2',
  target: 'EDR',
  automation: 'automatic',
  rules: ['increased_logging', 'behavioral_monitoring']
});

if (threat.type === 'PHISHING') {
  responses.push({
    action: 'EMAIL_BLOCK',
    priority: 'P1',
    target: 'Email Gateway',
    automation: 'automatic',
    scope: 'Domain Level'
  });
}

return responses.map(response => ({
  json: {
    threatId: threat.threatId,
    threatName: threat.name,
    severity: threat.assessedSeverity,
    ...response,
    generatedAt: new Date().toISOString(),
    workflowId: 'cybersentinel-automated-response-v1'
  }
}));

const threat = items[0].json;
const responses = [];

// Generate response actions for MEDIUM severity threats
responses.push({
  action: 'LOG_ANNOTATION',
  priority: 'P2',
  target: 'SIEM',
  automation: 'automatic',
  annotation: `Threat ${threat.threatId} detected - ${threat.name}`
});

responses.push({
  action: 'DOCUMENTATION',
  priority: 'P3',
  target: 'Threat Intel Database',
  automation: 'automatic',
  fields: ['threat_details', 'iocs', 'resolution_status']
});

responses.push({
  action: 'SCHEDULED_REVIEW',
  priority: 'P3',
  target: 'Security Team',
  automation: 'semi-automatic',
  schedule: 'Next Business Day'
});

return responses.map(response => ({
  json: {
    threatId: threat.threatId,
    threatName: threat.name,
    severity: threat.assessedSeverity,
    ...response,
    generatedAt: new Date().toISOString(),
    workflowId: 'cybersentinel-automated-response-v1'
  }
}));

const threat = items[0].json;

// Log for tracking - no immediate action needed for LOW severity
return [{
  json: {
    threatId: threat.threatId,
    threatName: threat.name,
    severity: threat.assessedSeverity,
    action: 'LOG_ONLY',
    priority: 'P4',
    note: 'Low severity threat - logged for trend analysis',
    generatedAt: new Date().toISOString(),
    workflowId: 'cybersentinel-automated-response-v1'
  }
}];

const response = items[0].json;

// Simulate patch deployment workflow
return [{
  json: {
    ...response,
    actionStatus: 'PATCH_DEPLOYMENT_INITIATED',
    details: {
      phases: [
        { phase: 'Pre-flight Check', status: 'completed' },
        { phase: 'Backup Verification', status: 'completed' },
        { phase: 'Patch Download', status: 'in_progress' },
        { phase: 'Staging Deployment', status: 'pending' },
        { phase: 'Production Deployment', status: 'pending' },
        { phase: 'Verification', status: 'pending' },
        { phase: 'Monitoring', status: 'pending' }
      ],
      estimatedCompletion: new Date(Date.now() + 3600000).toISOString(),
      rollbackWindow: '2 hours',
      affectedSystemsCount: response.target ? response.target.length : 1
    }
  }
}];

const response = items[0].json;

// Simulate network blocking for IOCs
return [{
  json: {
    ...response,
    actionStatus: 'NETWORK_BLOCKING_INITIATED',
    details: {
      devices: ['Firewall', 'Proxy', 'DNS Resolver', 'EDR'],
      blockTypes: ['IP Address', 'Domain', 'URL Pattern', 'File Hash'],
      scope: response.target || 'Enterprise Wide',
      duration: 'Until Threat Cleared',
      falsePositiveMitigation: {
        enabled: true,
        reviewWindow: '24 hours',
        autoExpire: '7 days'
      }
    }
  }
}];

const allResponses = items;

// Generate comprehensive incident report
const report = {
  reportId: 'INC-' + Date.now(),
  generatedAt: new Date().toISOString(),
  executiveSummary: {
    totalThreats: allResponses.length,
    criticalThreats: allResponses.filter(r => r.json.assessedSeverity === 'CRITICAL').length,
    highThreats: allResponses.filter(r => r.json.assessedSeverity === 'HIGH').length,
    mediumThreats: allResponses.filter(r => r.json.assessedSeverity === 'MEDIUM').length,
    lowThreats: allResponses.filter(r => r.json.assessedSeverity === 'LOW').length
  },
  threats: allResponses.map(r => ({
    threatId: r.json.threatId,
    threatName: r.json.threatName,
    severity: r.json.assessedSeverity,
    type: r.json.type,
    actionTaken: r.json.action,
    status: r.json.actionStatus || 'INITIATED'
  })),
  recommendations: [
    'Review and approve pending patch deployments',
    'Verify backup integrity for affected systems',
    'Conduct threat hunting for related IOCs',
    'Update security monitoring rules',
    'Schedule post-incident review'
  ],
  nextSteps: [
    'Monitor threat evolution over next 24 hours',
    'Review false positive reports',
    'Update threat intelligence feeds',
    'Conduct lessons learned session'
  ],
  workflowMetrics: {
    totalResponses: allResponses.length,
    automatedActions: allResponses.filter(r => r.json.automation === 'automatic').length,
    semiAutomatedActions: allResponses.filter(r => r.json.automation === 'semi-automatic').length,
    manualActions: allResponses.filter(r => r.json.automation === 'manual').length
  }
};

return [{ json: report }];

const report = items[0].json;

// Collect metrics for analytics
const metrics = {
  collectionTimestamp: new Date().toISOString(),
  workflowId: 'cybersentinel-automated-response-v1',
  runMetrics: {
    executionTime: new Date().toISOString(),
    threatsProcessed: report.executiveSummary.totalThreats,
    criticalCount: report.executiveSummary.criticalThreats,
    highCount: report.executiveSummary.highThreats,
    responseActionsGenerated: report.workflowMetrics.totalResponses,
    automationRate: report.workflowMetrics.automatedActions / report.workflowMetrics.totalResponses
  },
  threatTypeDistribution: {
    vulnerability: report.threats.filter(t => t.type === 'VULNERABILITY').length,
    ransomware: report.threats.filter(t => t.type === 'RANSOMWARE').length,
    apt: report.threats.filter(t => t.type === 'APT').length,
    phishing: report.threats.filter(t => t.type === 'PHISHING').length,
    malware: report.threats.filter(t => t.type === 'MALWARE').length
  },
  severityDistribution: {
    critical: report.executiveSummary.criticalThreats,
    high: report.executiveSummary.highThreats,
    medium: report.executiveSummary.mediumThreats,
    low: report.executiveSummary.lowThreats
  }
};

return [{ json: metrics }];
