const alert = items[0].json;

// Classify phishing campaign type
let campaignType = 'generic_phishing';
let severity = 'MEDIUM';
let indicatorsOfCompromise = [];

// Check for known campaign types
const campaignPatterns = {
  'holiday_phishing': /holiday|christmas|black friday|cyber monday|thanksgiving|new year/i,
  'tax_phishing': /tax|irs|income tax|tax declaration|tax refund/i,
  'delivery_phishing': /delivery|package|shipment|tracking|fedex|ups|dhl|royal mail|dpd/i,
  'invoice_fraud': /invoice|payment|overdue|statement|receipt|confirm payment/i,
  'gift_card_scam': /gift card|employee rewards|bonus|prize|winner/i,
  'oauth_consent': /consent|permission|access|authorize|app review/i,
  'crypto_phishing': /crypto|wallet|bitcoin|ethereum|defi|metamask/i,
  'business_email_compromise': /wire transfer|urgent|payment|sensitive|confidential|ceo fraud/i
};

for (const [type, pattern] of Object.entries(campaignPatterns)) {
  if (pattern.test(JSON.stringify(alert))) {
    campaignType = type;
    break;
  }
}

// Extract IOCs from alert
const extractedIOCs = {
  sender: alert.sender || alert.from || null,
  senderEmail: alert.sender_email || alert.from_address || null,
  subject: alert.subject || null,
  urls: alert.urls || alert.links || [],
  domains: alert.domains || [],
  attachments: alert.attachments || [],
  ipAddresses: alert.ip_addresses || [],
  redirectChains: alert.redirect_chain || []
};

// Check for AI-generated indicators
const aiGeneratedIndicators = {
  unusualLanguage: /formal russian business language|unusual formality|perfect grammar/i.test(JSON.stringify(alert)),
  personalization: alert.personalized === true,
  multiChannel: alert.channels?.length > 1
};

// Determine severity
if (campaignType === 'business_email_compromise' || campaignType === 'oauth_consent') {
  severity = 'HIGH';
} else if (campaignType === 'crypto_phishing' || campaignType === 'tax_phishing') {
  severity = 'HIGH';
} else if (aiGeneratedIndicators.unusualLanguage) {
  severity = 'MEDIUM';
}

return [{
  json: {
    alertId: 'PHISH-' + Date.now(),
    timestamp: new Date().toISOString(),
    campaignType,
    severity,
    source: alert.source || 'email_gateway',
    indicatorsOfCompromise: extractedIOCs,
    aiGenerated: aiGeneratedIndicators.unusualLanguage,
    targets: alert.targets || alert.recipients || [],
    subject: alert.subject,
    body: alert.body_summary || null,
    urls: extractedIOCs.urls,
    domains: extractedIOCs.domains
  }
}];

return [{
  json: {
    alertId: 'PHISH-2025-DEC-001',
    timestamp: new Date().toISOString(),
    campaignType: 'holiday_phishing',
    severity: 'HIGH',
    source: 'threat_intel',
    indicatorsOfCompromise: {
      domains: [
        'fake-delivery-tracking.com',
        'royalmail-delivery-notice.net',
        'fedex-package-verify.org',
        'ups-tracking-update.info'
      ],
      urls: [
        'https://fake-delivery-tracking.com/track?id=malicious',
        'https://royalmail-delivery-notice.net/verify?session=steal'
      ],
      senderEmail: ['support@fake-delivery-tracking.com', 'tracking@royalmail-delivery-notice.net']
    },
    aiGenerated: true,
    targets: ['retail', 'logistics', 'general users'],
    campaignStats: {
      emailsDetected: 33502,
      period: '2 weeks leading to Christmas',
      fakeAdsCreatedDaily: 10000,
      increasePercentage: 620
    }
  }
}];

return [{
  json: {
    alertId: 'PHISH-2025-DEC-002',
    timestamp: new Date().toISOString(),
    campaignType: 'crypto_phishing',
    severity: 'HIGH',
    source: 'threat_intel',
    indicatorsOfCompromise: {
      domains: ['tax-declaration-nl.com', 'belastingdienst-fake.nl'],
      urls: [
        'https://tax-declaration-nl.com/declare?wallet=drain',
        'https://belastingdienst-fake.nl/tax-refund?address=0xmalicious'
      ],
      senderEmail: ['noreply@belastingdienst.nl.fake', 'tax@tax-authority.eu.fake']
    },
    aiGenerated: false,
    targets: ['Dutch crypto holders', 'taxpayers'],
    campaignStats: {
      type: 'Crypto drainer',
      impersonation: 'European tax authorities',
      geography: 'Netherlands',
      target: 'Cryptocurrency holders'
    }
  }
}];

return [{
  json: {
    alertId: 'PHISH-2025-DEC-003',
    timestamp: new Date().toISOString(),
    campaignType: 'oauth_consent',
    severity: 'HIGH',
    source: 'threat_intel',
    indicatorsOfCompromise: {
      domains: ['consent-fix-verify.com', 'oauth-app-authorization.net'],
      urls: [
        'https://consent-fix-verify.com/authorize?app=malicious',
        'https://oauth-app-authorization.net/consent?scope=full'
      ],
      attachments: ['consent_form.pdf.exe', 'authorization_doc.scr']
    },
    aiGenerated: false,
    attackTechnique: 'ConsentFix - OAuth consent phishing with ClickFix-style user prompt',
    targets: ['Enterprise users', 'Microsoft 365 users', 'Google Workspace users']
  }
}];

// Aggregate all phishing data
const allCampaigns = [];

items.forEach(item => {
  if (item.json && item.json.alertId) {
    allCampaigns.push(item.json);
  }
});

// Group by campaign type
const campaignsByType = {};
allCampaigns.forEach(campaign => {
  if (!campaignsByType[campaign.campaignType]) {
    campaignsByType[campaign.campaignType] = [];
  }
  campaignsByType[campaign.campaignType].push(campaign);
});

// Calculate statistics
const stats = {
  totalCampaigns: allCampaigns.length,
  highSeverity: allCampaigns.filter(c => c.severity === 'HIGH').length,
  mediumSeverity: allCampaigns.filter(c => c.severity === 'MEDIUM').length,
  aiGenerated: allCampaigns.filter(c => c.aiGenerated).length,
  uniqueDomains: [...new Set(allCampaigns.flatMap(c => c.indicatorsOfCompromise?.domains || []))].length,
  uniqueUrls: [...new Set(allCampaigns.flatMap(c => c.indicatorsOfCompromise?.urls || []))].length
};

return [{
  json: {
    timestamp: new Date().toISOString(),
    campaigns: allCampaigns,
    campaignsByType,
    statistics: stats,
    summary: `${stats.totalCampaigns} phishing campaigns detected (${stats.highSeverity} HIGH severity, ${stats.aiGenerated} AI-generated)`
  }
}];

const data = items[0].json;

// Generate threat intelligence update
const threatIntelUpdate = {
  updateId: 'TI-PHISH-' + Date.now(),
  timestamp: new Date().toISOString(),
  source: 'CyberSentinel',
  feedType: 'phishing_campaigns',
  campaigns: data.campaigns.map(c => ({
    campaignId: c.alertId,
    type: c.campaignType,
    severity: c.severity,
    indicators: {
      domains: c.indicatorsOfCompromise?.domains || [],
      urls: c.indicatorsOfCompromise?.urls || [],
      senderEmails: c.indicatorsOfCompromise?.senderEmail || [],
      attachments: c.indicatorsOfCompromise?.attachments || []
    },
    metadata: {
      aiGenerated: c.aiGenerated || false,
      targets: c.targets || [],
      campaignStats: c.campaignStats || {}
    }
  })),
  statistics: data.statistics
};

return [{
  json: threatIntelUpdate
}];

const data = items[0].json;

// Generate domain blocking actions
const domainsToBlock = [...new Set(data.campaigns.flatMap(c => c.indicatorsOfCompromise?.domains || []))];
const urlsToBlock = [...new Set(data.campaigns.flatMap(c => c.indicatorsOfCompromise?.urls || []))];

const blockActions = {
  emailGateway: {
    domains: domainsToBlock,
    urls: urlsToBlock,
    action: 'quarantine',
    priority: data.campaigns.some(c => c.severity === 'HIGH') ? 'critical' : 'high'
  },
  webProxy: {
    domains: domainsToBlock,
    urls: urlsToBlock,
    action: 'block',
    priority: data.campaigns.some(c => c.severity === 'HIGH') ? 'critical' : 'high'
  },
  dnsFilter: {
    domains: domainsToBlock,
    action: 'block',
    nxdomain: true
  },
  sandbox: {
    urls: urlsToBlock,
    action: 'analyze',
    priority: 'high'
  }
};

return [{
  json: {
    ...data,
    blockingActions: blockActions,
    domainsBlocked: domainsToBlock.length,
    urlsBlocked: urlsToBlock.length,
    blockStatus: 'initiated'
  }
}];

