const data = items[0].json;

// Generate detection rules for email gateway and SIEM
const detectionRules = {
  emailGateway: [
    {
      ruleId: 'EMAIL-PHISH-001',
      name: 'Block holiday delivery phishing',
      conditions: [
        'subject CONTAINS "delivery" OR "package" OR "tracking"',
        'sender_domain NOT IN (fedex.com, ups.com, dhl.com, royalmail.com)',
        'body CONTAINS "click here" OR "verify now"'
      ],
      action: 'quarantine',
      enabled: true
    },
    {
      ruleId: 'EMAIL-PHISH-002',
      name: 'Block tax authority impersonation',
      conditions: [
        'sender_email CONTAINS "irs" OR "tax" OR "belastingdienst"',
        'sender_domain NOT IN (irs.gov, government.nl)',
        'body CONTAINS "crypto" OR "wallet" OR "refund"'
      ],
      action: 'quarantine',
      enabled: true
    },
    {
      ruleId: 'EMAIL-PHISH-003',
      name: 'Block OAuth consent phishing',
      conditions: [
        'subject CONTAINS "consent" OR "permission" OR "authorization"',
        'body CONTAINS "grant access" OR "review app"',
        'attachment_type IN (.pdf.exe, .scr, .lnk)'
      ],
      action: 'quarantine',
      enabled: true
    }
  ],
  siem: [
    {
      ruleId: 'SIEM-PHISH-001',
      name: 'Detect multiple failed login attempts post-phishing',
      conditions: ['authentication_events.failed_attempts > 5', 'time_window = 5m'],
      action: 'alert',
      severity: 'HIGH',
      enabled: true
    },
    {
      ruleId: 'SIEM-PHISH-002',
      name: 'Detect unusual OAuth consent grants',
      conditions: ['oauth_consent_grants.new_app = true', 'time_window = 1h', 'count > 3'],
      action: 'alert',
      severity: 'HIGH',
      enabled: true
    }
  ]
};

return [{
  json: {
    ...data,
    detectionRules,
    rulesUpdated: true,
    ruleCount: detectionRules.emailGateway.length + detectionRules.siem.length
  }
}];

const data = items[0].json;

// Generate SIEM correlation queries
const correlationQueries = {
  email: [
    'SELECT COUNT(*) FROM email_events WHERE sender_domain IN (' + 
      data.campaigns.flatMap(c => c.indicatorsOfCompromise?.domains || []).map(d => `"${d}"`).join(', ') + 
      ') AND timestamp > now() - 24h',
    'SELECT * FROM email_events WHERE subject LIKE "%delivery%" AND sender NOT IN (trusted_senders)'
  ],
  network: [
    'SELECT COUNT(*) FROM network_events WHERE dest_domain IN (' + 
      data.campaigns.flatMap(c => c.indicatorsOfCompromise?.domains || []).map(d => `"${d}"`).join(', ') + 
      ') AND timestamp > now() - 24h'
  ],
  authentication: [
    'SELECT * FROM authentication_events WHERE source_ip IN (' + 
      data.campaigns.flatMap(c => c.indicatorsOfCompromise?.ipAddresses || []).map(ip => `"${ip}"`).join(', ') + 
      ') AND timestamp > now() - 24h'
  ]
};

return [{
  json: {
    ...data,
    correlationQueries,
    queryCount: correlationQueries.email.length + correlationQueries.network.length + correlationQueries.authentication.length,
    correlationStatus: 'executed'
  }
}];

const data = items[0].json;

// Update threat intelligence database
const updateResult = {
  database: 'ThreatIntel',
  operation: 'upsert_phishing_campaigns',
  recordsUpdated: data.campaigns.length,
  iocs: {
    domains: data.campaigns.flatMap(c => c.indicatorsOfCompromise?.domains || []),
    urls: data.campaigns.flatMap(c => c.indicatorsOfCompromise?.urls || []),
    senderEmails: data.campaigns.flatMap(c => c.indicatorsOfCompromise?.senderEmail || [])
  },
  timestamp: new Date().toISOString(),
  status: 'success',
  version: '2025.12.1'
};

return [{
  json: updateResult
}];

const data = items[0].json;

// Generate comprehensive phishing report
const report = {
  reportId: 'PHISH-RPT-' + Date.now(),
  timestamp: new Date().toISOString(),
  period: 'December 2025',
  summary: {
    totalCampaigns: data.statistics.totalCampaigns,
    highSeverityCampaigns: data.statistics.highSeverity,
    mediumSeverityCampaigns: data.statistics.mediumSeverity,
    aiGeneratedCampaigns: data.statistics.aiGenerated,
    uniqueDomainsObserved: data.statistics.uniqueDomains,
    uniqueUrlsObserved: data.statistics.uniqueUrls
  },
  campaigns: data.campaigns.map(c => ({
    campaignId: c.alertId,
    type: c.campaignType,
    severity: c.severity,
    aiGenerated: c.aiGenerated || false,
    indicatorsCount: {
      domains: c.indicatorsOfCompromise?.domains?.length || 0,
      urls: c.indicatorsOfCompromise?.urls?.length || 0,
      senderEmails: c.indicatorsOfCompromise?.senderEmail?.length || 0
    },
    targets: c.targets || [],
    additionalData: c.campaignStats || c.attackTechnique || {}
  })),
  defenseActions: {
    domainsBlocked: data.domainsBlocked || 0,
    urlsBlocked: data.urlsBlocked || 0,
    detectionRulesUpdated: data.rulesUpdated || false,
    userNotificationsSent: true
  },
  trends: {
    holidayPhishingIncrease: '620% during Black Friday to Christmas',
    aiEnhancedPhishing: 'Significant increase in AI-generated content',
    multiChannelAttacks: 'SMS, WhatsApp, and email combinations'
  },
  recommendations: [
    'Deploy enhanced email filtering for holiday-themed phishing',
    'Implement user awareness training for delivery scams',
    'Monitor for OAuth consent anomalies',
    'Enable MFA on all accounts',
    'Block identified malicious domains and URLs',
    'Report suspicious emails via Phish Reporter tool'
  ],
  nextReview: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString()
};

return [{
  json: report
}];
